mapper.update:
update t_class
set status=2
where id = #{id}

mapper.select:
select id
from t_class
where id = #{id}
  and status != 2;

select id, teacher_id
from t_class
where invitation_code = #{code}
  and status != 2;

select id,
       name
from t_class
where id = #{id}
  and teacher_id = #{teacherId}
  and name = #{name}
  and status != 2
