mapper.update:
update t_class_student
set status=2
where class_id = #{classId}

update t_class_student
set status = 2
where class_id = #{classId}
  and student_id = #{studentId}

mapper.select:
select id
from t_class_student
where class_id = #{classId}
  and student_id = #{studentId}
  and status != 2
